import Header from "./Header";
import { useState } from "react";
import { useHistory } from "react-router-dom";

function AddProducts() {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const history = useHistory();

  async function addProduct() {
    console.warn(name, price, description);
    const formData = new FormData();

    formData.append("price", price);
    formData.append("name", name);
    formData.append("description", description);
     await fetch("http://localhost:8000/api/addProduct", {
      method: "POST",
      body: formData,
    });
    alert("Product added!");
    history.push("/");
  }




  async function getData() {
    const formData = new FormData();
    formData.append("price", price);
    formData.append("name", name);
    formData.append("description", description);
    let user = JSON.parse(localStorage.getItem('user-info'));
    const token = user.data;
    let result = await fetch('http://localhost/directus/public/tokoonline/items/products', {
      method: "POST",
      body: formData,
      headers: {
        Authorization: `Bearer ${token}`
      }
    });

     result = await result.json();
      const tes = result.data ;
      console.log('consresult>>', tes);
      alert("Product added!");
      history.push("/");
    }

  return (
    <div>
      <Header />
      <h1>Add Products</h1>
      <div className="col-sm-6 offset-sm-3">
        <br />
        <input
          type="text"
          className="form-control"
          placeholder="Name"
          onChange={(e) => setName(e.target.value)}
        ></input>

        <input
          type="text"
          className="form-control"
          placeholder="Price"
          onChange={(e) => setPrice(e.target.value)}
        ></input>
        <textarea
          className="form-control"
          placeholder="Description"
          onChange={(e) => setDescription(e.target.value)}
        ></textarea>
        <br></br>
        <button className="btn btn-primary" onClick={addProduct}>
          Add Product
        </button>
      </div>
    </div>
  );
}
export default AddProducts;
