import Header from "./Header";
import { withRouter } from "react-router-dom";
import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

function UpdateProducts(props) {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const history = useHistory();

  console.warn("props", props.match.params.id);
  const [data, setData] = useState([]);

  useEffect(async () => {
    try{
      let user = JSON.parse(localStorage.getItem('user-info'));
      const token = user.data.token;
      let result = await fetch(
        "http://localhost/directus/public/tokoonline/items/products/" + props.match.params.id, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`
        }});
      result = await result.json();
      const tes = result.data ;
      console.log('tes', tes);
      setData(tes);
      setName(tes.name);
      setPrice(tes.price);
      setDescription(tes.description);
    }
    catch(e)
    {
      localStorage.remove();
    }
  }, []);

  async function editProduct(id) {
    let formData = {
      name: name,
      price: price,
      description: description
    };

    // let formData = new FormData();
    // formData.append("price", price);
    // formData.append("name", name);
    // formData.append("description", description);

    console.log('formData',formData);

    let user = JSON.parse(localStorage.getItem('user-info'));
    const token = user.data.token;


     await fetch("http://localhost/directus/public/tokoonline/items/products/" + id,{
        method: "PATCH",
        body: formData,
        headers: {
          Authorization: `Bearer ${token}`,
        }
      }).then(response =>response.json()
      .then(result => console.log('ini hasilnya: ',result)));

      console.log('id',id);



    alert("Product has been updated!");
    history.push("/");
  }

  return (
    <div>
      <Header />
      <h1>Update Products</h1>
      <div className="col-sm-6 offset-sm-3">
        <br />
        <input
          className="form-control"
          type="text"
          name="name"
          defaultValue={data.name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="form-control"
          type="text"
          name="price"
          defaultValue={data.price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <input
          className="form-control"
          type="text"
          name="description"
          defaultValue={data.description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <br />
        <br />
        <br />
        <button className="btn btn-danger" onClick={() => editProduct(data.id)}>
          Update Product
        </button>
      </div>
    </div>
  );
}
export default withRouter(UpdateProducts);
