import Header from "./Header";
import React, { useState, useEffect } from "react";
import { Table } from "react-bootstrap";
import { Link } from "react-router-dom";




function ProductList() {
  let data = {
    "data": [
        {
            "id": 3,
            "name": "mie goreng",
            "price": 200023434,
            "description": "sdfsdaqweqweq",
            "created_at": "2021-12-26",
            "updated_at": "2021-12-26"
        },
        {
            "id": 5,
            "name": "mie ayan",
            "price": 4000,
            "description": "sdfsdf",
            "created_at": "2021-12-26",
            "updated_at": "2021-12-26"
        }
    ],
    "public": true
}
// let myObj = JSON.parse(exdata);
console.log('exdata',data);






  const [datahghjg, setData] = useState([]);
  useEffect(() => {
    getData();
  }, []);
  // console.warn("result", data);

  async function deleteOperation(id) {
    if (
      window.confirm(
        "Are you sure you want to delete product with id = " + id + " ?"
      )
    ) {
      let result = await fetch("http://localhost:8000/api/delete/" + id, {
        method: "DELETE",
      });
      result = await result.json();
      console.warn(result);
      getData();
      alert("Product has been deleted");
    } else {
      getData();
    }
  }

  async function getData() {
    var myHeaders = new Headers();
    myHeaders.append("Authorization", "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpZCI6MSwiZXhwIjoxNjQwNTc1MDgwLCJ0dGwiOjIwLCJ0eXBlIjoiYXV0aCIsImtleSI6IjlhZTJhZTgwLWE2ZjgtNGJiNy05ODEyLTI2NDJhYzlhMzZlZiIsInByb2plY3QiOiJ0b2tvb25saW5lIn0.YqmB-eYHw7qHeUt2lUHjERD6GqoEwSUSLyP6j-LXAyI");
    myHeaders.append("Cookie", "PHPSESSID=v45pu2o90okb37831obcrmaaqt");

    var raw = "";

    var requestOptions = {
      method: 'GET',
      headers: myHeaders,
      body: raw,
      redirect: 'follow'
    };

    let result = fetch("http://localhost/directus/public/tokoonline/items/products", requestOptions)
      .then(response => response.text())
      .then(result => console.log(result))
      .catch(error => console.log('error', error));
      console.log('tes',result);
    setData(result);

  }

  return (
    <div>
      <Header />
      <h1>Product Lists</h1>
      <div className="col-sm-8 offset-sm-2">
        <Table solid bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Product Name</th>
              <th>Price</th>
              <th>Description</th>
              <th>Image</th>
              <th>Action</th>
            </tr>
          </thead>

          {data.map((item) => (
            <tbody>
              <tr>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.price}</td>
                <td>{item.description}</td>
                <td>
                  <img
                    style={{ width: 100 }}
                    src={"http://localhost:8000/" + item.file_path}
                  ></img>
                </td>
                <td>
                  <span
                    className="btnDelete"
                    onClick={() => deleteOperation(item.id)}
                  >
                    Delete
                  </span>
                  <Link to={"update/" + item.id}>
                    <span className="btnUpdate">Update</span>
                  </Link>
                </td>
              </tr>
            </tbody>
          ))}
        </Table>
      </div>
    </div>
  );
}

export default ProductList;
