import Header from "./Header";
import { withRouter } from "react-router-dom";
import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

function UpdateProducts(props) {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const history = useHistory();

  console.log("props", props.match.params.id);
  const [data, setData] = useState([]);
  console.log("hasil-useEffect", setData);

  useEffect(async () => {
    try {
      let user = JSON.parse(localStorage.getItem("user-info"));
      const token = user.data.token;
      fetch(
        "http://localhost/directus/public/tokoonline/items/products/" +
          props.match.params.id,
        {
          method: "GET",
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
        .then((res) => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
            setItems(result.data);
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        );
      // result = await result.json();
      // const tes = result.data;
      // console.log("useEffect", tes);
      // setData(tes);
      // setName(tes.name);
      // setPrice(tes.price);
      // setDescription(tes.description);
    } catch (e) {
      localStorage.clear();
    }
  }, []);

  async function editProduct(id) {
    let formSimpan = {
      name: name,
      price: price,
      description: description,
    };

    console.log("JSON.stringify", JSON.stringify(formSimpan));

    let user = JSON.parse(localStorage.getItem("user-info"));
    const token = user.data.token;

    await fetch(
      "http://localhost/directus/public/tokoonline/items/products/" + id,
      {
        method: "PATCH",
        body: JSON.stringify(formSimpan),
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      }
    )
      .then((response) => response.json())
      .then((result) => console.log("ini hasilnya: ", result))
      .catch((error) => console.log("error", error));

    console.log("datadata", data);

    alert("Product has been updated!");
    history.push("/");
  }

  return (
    <div>
      <Header />
      <h1>Update Products</h1>
      <div className="col-sm-6 offset-sm-3">
        <br />
        <input
          className="form-control"
          type="text"
          name="name"
          defaultValue={data.name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="form-control"
          type="text"
          name="price"
          defaultValue={data.price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <input
          className="form-control"
          type="text"
          name="description"
          defaultValue={data.description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <br />
        <br />
        <br />
        <button className="btn btn-danger" onClick={() => editProduct(data.id)}>
          Update Product
        </button>
      </div>
    </div>
  );
}
export default withRouter(UpdateProducts);
